package com.github.pay.alipayimport android.text.TextUtilsinternal class AliPayResult private constructor() {    var resultStatus: String = ""        private set    var result: String = ""        private set    var memo: String = ""        private set    fun parseResult(rawResult: Map<String, String>?): AliPayResult {        rawResult?.keys?.forEach { key ->            when {                TextUtils.equals(key, "resultStatus") -> resultStatus = rawResult[key] ?: ""                TextUtils.equals(key, "result") -> result = rawResult[key] ?: ""                TextUtils.equals(key, "memo") -> memo = rawResult[key] ?: ""            }        }        return this    }    companion object {        fun create(): AliPayResult {            return AliPayResult()        }    }}